function update_ratings_average(rating_count) {
  var rating_subtotal = 0;
  var ratings_used = 0;
  if(rating_count > 0) {
    for (i=1; i <= rating_count; i++) {
      var slider_val = document.getElementById("rating_value_"+i).innerHTML;
      if(slider_val != "Not Rated") {
        rating_subtotal = rating_subtotal + parseFloat(slider_val);
        ratings_used = ratings_used + 1;
      }
    }
    var average;
    if(ratings_used > 0) {
      average = (rating_subtotal/ratings_used);
      average = average.toFixed(1);
    } else {
      average = "N/A";
    }
    return average;
  }
}


