Position.GetWindowSize = function() {
	var width, height;
        this.width = window.innerWidth || ( document.documentElement.clientWidth || document.body.clientWidth );
        this.height = window.innerHeight || ( document.documentElement.clientHeight || document.body.clientHeight );
	return this;
}

Position.GetWindowScroll = function() {
	var scrollLeft, scrollTop;
        this.scrollLeft = self.pageXOffset || ( document.documentElement.scrollLeft || document.body.scrollLeft );
        this.scrollTop = self.pageYOffset || ( document.documentElement.scrollTop || document.body.scrollTop );
	return this;
}

function showhint(menucontents, obj, e, tipwidth)
{
// Get the event object.
	var e = ( e || event );

// Set the mouse events on the object the event is coming from to hide the tooltip.
	obj.onmouseout = hidehint;
	obj.onmouseup = hidehint;

	var tooltipdiv = $("hintbox");
	tooltipdiv.style.width = parseInt(tipwidth) + "px";
	tooltipdiv.style.position = "absolute";
	tooltipdiv.style.zIndex = 1000;

	if ( tooltipdiv.style.display != "block" )
	{
		tooltipdiv.style.visibility = "visible";
		tooltipdiv.innerHTML = menucontents;
	}

	tooltipdiv.style.display = "block";

	var tooltipwidth = Element.getDimensions(tooltipdiv).width;
	var tooltipheight = Element.getDimensions(tooltipdiv).height;

// Distance between the mouse pointer and the closest corner of the tooltip div.
	var mouseoffset = 8;

// How many pixels near the edge of the window before the tooltip div changes directions.
	var edgeoffset = 12;

// Scroll offset.
	var windowScroll = Position.GetWindowScroll();

// Height and width of the screen.
	var windowSize = Position.GetWindowSize();

// Get the mouse coordinates.
	var xcoord = mouseoffset + Event.pointerX(e);
	var ycoord = mouseoffset + Event.pointerY(e);

	if ( tooltipwidth < windowSize.width )
	{
		if ( ( ( xcoord + edgeoffset + tooltipwidth ) >= windowSize.width + windowScroll.scrollLeft ) && ( ( xcoord - ( mouseoffset * 2 ) - edgeoffset - tooltipwidth ) > windowScroll.scrollLeft ) )
		{
			xcoord = ( xcoord - ( mouseoffset * 2 ) - tooltipwidth );
		}
	}

	if ( tooltipheight < windowSize.height )
	{
		if ( ( ( ycoord + edgeoffset + tooltipheight ) >= windowSize.height + windowScroll.scrollTop ) && ( ( ycoord - ( mouseoffset * 2 ) - edgeoffset - tooltipheight ) > windowScroll.scrollTop ) )
		{
			ycoord = ( ycoord - ( mouseoffset * 2 ) - tooltipheight );
		}
	}

	tooltipdiv.style.left = xcoord + "px";
	tooltipdiv.style.top = ycoord + "px";
}

function hidehint()
{
	if ( $("hintbox") )
	{
		$("hintbox").style.display = "none";
	}
}

function createhintbox()
{
	var divblock=document.createElement("div");
	divblock.setAttribute("id", "hintbox");
	divblock.onmouseover=hidehint;
	document.body.appendChild(divblock);
	hidehint();
}

if ( window.addEventListener )
	window.addEventListener("load", createhintbox, false);
else if ( window.attachEvent )
	window.attachEvent("onload", createhintbox);
else if ( document.getElementById )
	window.onload=createhintbox;

function clear_site_tab(targetobj) {
  var ullist = targetobj.parentNode.getElementsByTagName("li")

  for (var i = 0; i < ullist.length; i++) {
      ullist[i].className = ""                //deselect all tabs
  }
}

function change_site_tab(targetobj) {
	clear_site_tab(targetobj);
  targetobj.blur()                        // get rid of the dotted line
  targetobj.className = "activelink" //highlight currently clicked on tab
}

function dropdown_init (targetobj) {
	if(navigator.appName=="Microsoft Internet Explorer") {
		// only need this for IE it seems
		navRoot = targetobj;
		for (i=0; i < navRoot.childNodes.length; i++) {
			node = navRoot.childNodes[i];
			if (node.nodeName=="LI") {
				node.onmouseover=function() {
					this.className+=" over";
				}
				node.onmouseout=function() {
					this.className=this.className.replace(" over", "");
				}
			}
		}
	}
}


