// Show/hide "loading" overlay before and after ajax request
function ajaxHandler(carousel, status) {
  var overlay = $(carousel.carouselElemID + '-overlay');
  if (status == "before") {
    if (overlay) {
      overlay.setOpacity(0);
      overlay.show();
      Effect.Fade(overlay, {from: 0, to: 0.8, duration: 0.2})
    }
    else
      new Insertion.Top(carousel.carouselElemID, "<div id='" + carousel.carouselElemID + "-overlay' >Loading...<br><img src='/images/carousel-loader.gif'></div>");
  }
  else 
    Effect.Fade(overlay, {from: 0.8, to: 0.0, duration: 0.2})
}

function switchImage(image, answerCount) {
	if (document.images) {
		if (document.images[image]) {
			var startImageName = "A";
			for (var i = 0; i < answerCount; i++) {
				var imgName = startImageName.charCodeAt(0) + i;
				var regularImageName = String.fromCharCode(imgName);
				if (document.images[regularImageName]) {
					var regularImage = new Image();
					regularImage.src = "images/"  + regularImageName + ".gif";
					document.images[regularImageName].src = regularImage.src;
					//alert(("lightOn('" + regularImageName + "');") + " : " + document.images[regularImageName].onmouseover);
					switch (regularImageName) {
						case 'A':
							document.images[regularImageName].onmouseover = function(){eval("lightOn('A');");};
							document.images[regularImageName].onmouseout = function(){eval("lightOff('A');");};
						break

						case 'B':
							document.images[regularImageName].onmouseover = function(){eval("lightOn('B');");};
							document.images[regularImageName].onmouseout = function(){eval("lightOff('B');");};
						break

						case 'C':
							document.images[regularImageName].onmouseout = function(){eval("lightOff('A');");};
							document.images[regularImageName].onmouseover = function(){eval("lightOn('C');");};
						break

						case 'D':
							document.images[regularImageName].onmouseover = function(){eval("lightOn('D');");};
							document.images[regularImageName].onmouseout = function(){eval("lightOff('D');");};
						break

						case 'E':
							document.images[regularImageName].onmouseover = function(){eval("lightOn('E');");};
							document.images[regularImageName].onmouseout = function(){eval("lightOff('E');");};
						break

						case 'F':
							document.images[regularImageName].onmouseover = function(){eval("lightOn('F');");};
							document.images[regularImageName].onmouseout = function(){eval("lightOff('F');");};
						break
					}
				}	
			}
			
			var imgGreen = new Image();
			imgGreen.src = "images/" + image + "_green.gif";
			document.images[image].src = imgGreen.src;
			document.images[image].onmouseover = null
			document.images[image].onmouseout = null
		}
	}
}

function lightOn(image) {
	if (document.images) {
		if (document.images[image]) {
			var imgGreen = new Image();
			imgGreen.src = "images/" + image + "_green.gif";
			document.images[image].src = imgGreen.src;
		}
	}
}

function lightOff(image) {
	if (document.images) {
		if (document.images[image]) {
			var imgRegular = new Image();
			imgRegular.src = "images/" + image + ".gif";
			document.images[image].src = imgRegular.src;
		}
	}
}

function toggleTagImage(image, toImage) {
	if (document.images) {
		for (i = 0; i < document.images.length; i++) {
			if (image == document.images[i].name) {
				var imgNew = new Image();
				imgNew.src = "images/" + toImage + ".jpg";
				document.images[i].src = imgNew.src;
				break;
			}
		} 
	}
}
